mapper.select:
SELECT COUNT(*) FROM thumb_up WHERE
-- 如果传进来的是用户就返回用户的点赞数
user_id = #{uid}
-- 如果传进来是博客就返回博客的点赞数
blog_id = #{bid}

SELECT user.is_ban
FROM user
WHERE user.uid = #{userid}

mapper.update:
UPDATE blog
SET blog.is_edit = 1
WHERE blog.bid = #{bid}

mapper.select:
SELECT tuid
FROM thumb_up
WHERE
    user_id = #{user_id}
    AND
    blog_id = #{blog_id}
