mapper.select:
SELECT *
FROM discuss
WHERE did = #{id}
AND visibility=#{visibility}

SELECT *
FROM discuss d
WHERE did=#{did}
AND user_id=#{userid}

SELECT * FROM blog_discuss d LEFT JOIN blog b ON d.blog_id = b.bid JOIN user u ON b.user_id = u.uid WHERE d.discuss_id = #{did} AND b.type = 0
AND b.content LIKE "%"#{key}"%" OR u.nickname LIKE "%"#{key}"%"
GROUP BY d.blog_id LIMIT #{startNum},#{endNum}

mapper.insert:
INSERT INTO discuss (name, user_id, detail, start_time)
SELECT #{name}, #{user_id}, #{detail}, #{start_time} FROM DUAL
WHERE NOT EXISTS(SELECT did FROM discuss WHERE name=#{name});

mapper.update:
UPDATE discuss SET
name = #{name}, user_id = #{user_id}, detail = #{detail}, start_time = #{start_time}, end_time = #{end_time}, is_edit = #{is_edit}, release_time = #{release_time}, visibility = #{visibility}
WHERE did = #{id}
