mapper.select:
SELECT u.*, (SELECT COUNT(f.fid) FROM follow as f WHERE u.uid=f.followed_id AND type=0) AS follerNum, (SELECT COUNT(f.fid) FROM follow as f WHERE u.uid=f.user_id AND type=0) AS folledNum FROM user AS u WHERE u.alive = 0
AND(u.nickname LIKE "%"#{keyword}"%" OR u.motto LIKE "%"#{keyword}"%")
LIMIT #{startNum},#{endNum}

mapper.update:
UPDATE user
SET weight = #{weight}
WHERE
uid = #{uid}
